INSERT INTO `locales_page_text` VALUES ('1171', null, null, null, null, null, null, null, '<HTML>\r\n<BODY>\r\n<IMG src=\"Interface\\Pictures\\9330_Gammerita_sepia_256\"/>\r\n</BODY>\r\n</HTML>');
INSERT INTO `locales_page_text` VALUES ('3547', null, null, null, null, null, null, null, '<Похоже, что Аркавон вел дневник. Он, кажется, на нескольких языках.>\r\n\r\nДень 3613785:\r\n\r\nАркавон бросил дневник. Лучше поглядит как человечки дерутся. Когда перестанут, Аркавон проснется и увидит, кто первым тронет сферу. Пока что побеждали:\r\n\r\nОрда:  \r\n Защитили: $4024w\r\n Захватили: $4022w\r\n\r\nАльянс:\r\n Защитили: $4025w\r\n Захватили: $4023w\r\n\r\nДень 3613760:\r\n\r\nНочью Аркавон выскользнул наружу и собрал доспехи. Аркавон починит. Трудно.');
INSERT INTO `locales_page_text` VALUES ('3548', null, null, null, null, null, null, null, 'День 3613725:\r\n\r\nЧеловечки пришли в Ледяные Оковы из-за океана. Каждый день по многу битв. Аркавон следит! \r\n\r\nДень 2145617:\r\n\r\nСнаружи все трясется. Человечки суетятся. Аркавон посидит дома.\r\n\r\nДень 1051213:\r\n\r\nАркавон проснулся, заметил, что лицо стало мягким. Ничего. Снова спатьки.');
INSERT INTO `locales_page_text` VALUES ('3549', null, null, null, null, null, null, null, 'День 900500:\r\n\r\nСнаружи скукотища. Аркавон баиньки. Снаружи шумит, холодно. \r\n\r\nДень 10500: \r\n\r\nАркавону скучно писать дневник. Отдохну.\r\n\r\nДень 10:\r\n\r\nТворцы ушли. Аркавона оставили сторожить.');
INSERT INTO `locales_page_text` VALUES ('3546', null, null, null, null, null, null, null, 'Первая половина дневника - всего лишь амбарная книга мистера Купера. Колонки цифр, относящихся к производству и сбыту товаров жителям Луноречья и даже нескольким штормградским горожанам.$B$Bво второй половине тетради - дневник Купера, где он говорит о том, что оставляет семейное дело младшему брату. Здесь упоминаются и дети.');
